script:
(function () {
  const rootId = 'milk-workspace';

  function ready(cb) {
    if (document.readyState === 'complete' || document.readyState === 'interactive') cb();
    else document.addEventListener('DOMContentLoaded', cb, { once: true });
  }

  function canUseFrappe() {
    return typeof window.frappe !== 'undefined' && typeof frappe.set_route === 'function';
  }

  function routeByHash(hash) {
    // hash example: "#/app/driver-report"
    if (!hash || hash[0] !== '#') return;

    // Parse after "#"
    const h = hash.slice(1); // "/app/driver-report"
    const parts = h.split('/').filter(Boolean); // ["app","driver-report",...]
    if (parts[0] !== 'app') {
      // Non-standard; just set hash
      window.location.hash = hash;
      return;
    }

    // query-report
    if (parts[1] === 'query-report' && parts[2]) {
      const report_name = decodeURIComponent(parts.slice(2).join('/'));
      if (canUseFrappe()) frappe.set_route('query-report', report_name);
      else window.location.hash = `#/app/query-report/${encodeURIComponent(report_name)}`;
      return;
    }

    // List views: /app/<doctype>/view/list
    if (parts.length >= 4 && parts[3] === 'list' && parts[2] === 'view') {
      const doctype_slug = parts[1]; // e.g., "supplier-loan"
      // Try to restore DocType name format (Title Case) if frappe is available
      const toTitle = (s) => s.split('-').map(w => w.charAt(0).toUpperCase() + w.slice(1)).join(' ');
      if (canUseFrappe()) {
        frappe.set_route('List', toTitle(doctype_slug), 'List');
      } else {
        window.location.hash = hash;
      }
      return;
    }

    // Simple pages: /app/<page>
    if (parts.length >= 2) {
      const page = parts[1];
      if (canUseFrappe()) frappe.set_route(page);
      else window.location.hash = hash;
      return;
    }

    // Fallback
    window.location.hash = hash;
  }

  function bind(root) {
    if (!root) return;

    // Optional brand color
    const brand = root.getAttribute('data-brand');
    if (brand) root.style.setProperty('--mw-brand', brand);

    // Delegated click/keyboard
    root.addEventListener('click', (e) => {
      const card = e.target.closest('.mw-card');
      if (!card) return;
      e.preventDefault();
      const href = card.getAttribute('data-href');
      routeByHash(href);
    });

    root.addEventListener('keyup', (e) => {
      if (e.key !== 'Enter' && e.key !== ' ') return;
      const card = e.target.closest('.mw-card');
      if (!card) return;
      e.preventDefault();
      const href = card.getAttribute('data-href');
      routeByHash(href);
    });

    // Accessibility
    root.querySelectorAll('.mw-card').forEach(card => {
      card.setAttribute('role', 'button');
      card.setAttribute('tabindex', '0');
      const label = card.querySelector('.mw-label')?.textContent?.trim() || 'link';
      card.setAttribute('aria-label', label);
    });
  }

  ready(() => bind(document.getElementById(rootId)));
})();

style:
/* Core theme */
.mw-container {
  --brand: var(--mw-brand, #10b981);
  --text: #0f172a;
  --muted: #64748b;
  --border: #e2e8f0;
  --surface: #f8fafc;
  color: var(--text);
}

/* Sections */
.mw-section { margin-bottom: 22px; }
.mw-section-title {
  display: flex; align-items: center; gap: 8px;
  font-size: 14px; color: var(--muted); margin: 0 0 10px;
}
.mw-section-icon {
  width: 22px; height: 22px; border-radius: 6px;
  display: inline-flex; align-items: center; justify-content: center;
  background: var(--surface); color: var(--brand); border: 1px solid var(--border); font-size: 13px;
}

/* Grid */
.mw-grid { display: grid; grid-template-columns: repeat(4, minmax(0, 1fr)); gap: 14px; }
@media (max-width: 1200px) { .mw-grid { grid-template-columns: repeat(3, 1fr); } }
@media (max-width: 820px)  { .mw-grid { grid-template-columns: repeat(2, 1fr); } }
@media (max-width: 480px)  { .mw-grid { grid-template-columns: 1fr; } }

/* Card base */
.mw-card {
  position: relative;
  display: flex; flex-direction: column; align-items: center; justify-content: center;
  gap: 10px; min-height: 112px;
  text-decoration: none; color: inherit;
  border: 1px solid var(--border); border-radius: 16px;
  background: #ffffff;
  overflow: hidden;
  transform: translateZ(0);
  will-change: transform, box-shadow, border-color;
  transition: transform .18s ease, box-shadow .25s ease, border-color .18s ease, background .18s ease;
  box-shadow: 0 1px 1px rgba(2, 6, 23, 0.04), 0 8px 24px rgba(2, 6, 23, 0.06);
}

/* Animated shimmer layer */
.mw-card::before {
  content: "";
  position: absolute; inset: 0;
  background: radial-gradient(120% 60% at 100% 0%, rgba(16,185,129,.10) 0%, rgba(16,185,129,0) 60%),
              linear-gradient(120deg, rgba(255,255,255,0) 20%, rgba(255,255,255,.55) 50%, rgba(255,255,255,0) 80%);
  opacity: 0;
  transform: translateX(-20%);
  transition: opacity .35s ease, transform .35s ease;
  pointer-events: none;
}

/* Glow ring on hover */
.mw-card::after {
  content: "";
  position: absolute; inset: -1px;
  border-radius: 18px;
  box-shadow: 0 0 0 0 rgba(16,185,129,.28);
  opacity: 0; pointer-events: none;
  transition: box-shadow .25s ease, opacity .2s ease;
}

/* Hover / focus animation */
.mw-card:hover,
.mw-card:focus-visible {
  transform: translateY(-6px) scale(1.02);
  box-shadow: 0 16px 40px rgba(2, 6, 23, 0.16);
  border-color: color-mix(in srgb, var(--brand) 35%, var(--border));
  background: linear-gradient(180deg, rgba(248,250,252,1), rgba(255,255,255,1));
}
.mw-card:hover::before,
.mw-card:focus-visible::before {
  opacity: 1;
  transform: translateX(0);
}
.mw-card:hover::after,
.mw-card:focus-visible::after {
  opacity: 1;
  box-shadow: 0 0 0 6px rgba(16,185,129,.18), 0 10px 30px rgba(16,185,129,.25) inset;
}

/* Card content */
.mw-card-icon { font-size: 28px; line-height: 1; transform: translateZ(0); transition: transform .2s ease; }
.mw-card-label { font-weight: 800; text-align: center; font-size: 14px; line-height: 1.3; color: #0b1324; padding-inline: 8px; }

/* Slight icon bob on hover */
.mw-card:hover .mw-card-icon,
.mw-card:focus-visible .mw-card-icon { transform: translateY(-2px) scale(1.05); }

/* Reduced motion support */
@media (prefers-reduced-motion: reduce) {
  .mw-card, .mw-card::before, .mw-card::after, .mw-card-icon {
    transition: none !important;
    animation: none !important;
  }
}
