(function () {
  // Background image path (ensure this file exists and is built)
  const BG_SRC = '/assets/milk/immages/23.png';

  function ensureStyle() {
    // If you need to inject any extra CSS dynamically, you can do it here.
    // Not required now since we ship a separate CSS file.
  }

  function ensureBgLayer() {
    let bg = document.querySelector('body > .mw-bg-layer');
    if (!bg) {
      bg = document.createElement('div');
      bg.className = 'mw-bg-layer';
      document.body.prepend(bg);
    }
    return bg;
  }

  function applyBackground() {
    const bg = ensureBgLayer();
    const img = new Image();
    img.onload = () => {
      bg.style.backgroundImage = "url('" + BG_SRC + "')";
      // console.log('[MW] Background loaded:', BG_SRC);
    };
    img.onerror = () => {
      console.warn('[MW] Background NOT found:', BG_SRC);
      bg.style.backgroundImage = '';
      bg.style.backgroundColor = '#f7fafc';
    };
    // Cache-busting query to avoid stale caches during development
    img.src = BG_SRC + '?v=' + Date.now();
  }

  function run() {
    requestAnimationFrame(applyBackground);
  }

  // Initial run
  if (document.readyState === 'loading') {
    document.addEventListener('DOMContentLoaded', run, { once: true });
  } else {
    run();
  }

  // Re-apply on workspace re-renders or internal navigation
  try {
    const mo = new MutationObserver(() => {
      if (!document.querySelector('body > .mw-bg-layer')) run();
    });
    mo.observe(document.documentElement, { childList: true, subtree: true });
    window.addEventListener('hashchange', run);
    document.addEventListener('frappe.router.change', run);
  } catch (e) {
    // Environments without those events are fine
  }
})();

